                                 <div className="card-bottom">
                                    <img className='small-img' src={smallImg} alt="" />
                                    <img className='small-img' src={smallImg} alt="" />
                                    <img className='small-img' src={smallImg} alt="" />
                                    <button onClick={() => onAdd(product)} className='buy' >Купить ингредиенты</button>
