import React from 'react'

export default function Bascet(props) {
 
  const { cartItems, onAdd, onRemove } = props ;
  const itemPrice = cartItems.reduce((a, c) => a + c.price * c.qty, 0);
  const {countCartItems} = props
  return (
    <div className='basket-cont'>
      <div>{cartItems.length === 0 && <div className='bascet'>Cart is Bush</div>}</div>
        {cartItems.map((item) => (
         
              <div className='d-flex-3 basket-items' key={item.id} >

                <div className='item-img'></div>
                <div className="butt">
                <button onClick={()=>onAdd(item)} className='add'>+</button>
                <div>{item.qty}</div>
                <button onClick={()=>onRemove(item)} className='remove'>-</button>
              
                </div>
                <div className="item-info">
                  <div className='item-name' >{item.name}</div>
                <div className="item-price">{item.qty * item.price}руб.</div>
                </div>
                
                <div>
                {/* {item.qty} x ${item.price.toFixed(2)} */}
              </div>
              </div>
            
          
        ))}

        <div className="total">
          <div className="total-price">
            <h4>Итого,руб:</h4>
            <p>{itemPrice}</p>
          </div>
          {/* <button></button> */}
          <a href="#/cart">
            Подтвердить заказ {' '}
            countCartItems?
          </a>
        </div>
    </div>
    // </div>
  )
}
