import React from "react";
import { useContext } from "react";
import { ShopContext } from "../context";

function Cart() {
  const { order, handleBasketShow } = useContext(ShopContext);
  return (
    <div className="cart " onClick={handleBasketShow}>
      <i style={{background:"#EE6E73",padding:"5px"}} className="material-icons">add_shopping_cart</i>
      {order.length ? (
        <span className="card-quantity">{order.length}</span>
      ) : null}
    </div>
  );
}
export default Cart;
