import React from 'react'
import Pro from '../Pro'
export default function Products(props) {
  // const data = {
  //   products: [
  //     {
  //       id: '1',
  //       image: "./chicken.png",
  //       name: 'Рождественский сюрпризво фритюре',
  //       time: '15 минут',
  //       rate: 4.2,

  //     },
  //     {
  //       id: '1',

  //       image: "./chicken.png",
  //       name: 'Рождественский сюрпризво фритюре',
  //       time: '15 минут',
  //       rate: 1.2,

  //     },
  //     {
  //       id: '1',

  //       image: "./chicken.png",
  //       name: 'Рождественский сюрпризво фритюре',
  //       time: '10 минут',
  //       rate: 3,

  //     },
  //   ]
  // }

  const {onAdd, product} = props

  const data = [
    {
      id: '1',
      image: "https://i.imgur.com/QT78Hua.jpg",
      name: 'Рождественский сюрпризво фритюре',
      time: '15 минут',
      rate: 4.2,
      price: 120,
      smallImg: 'https://avatars.mds.yandex.net/i?id=39aa110b1a018f6b45381716ab892efffd0ded84-5235444-images-thumbs&n=13' 
    },
    {
      id: '1',
      image: "https://avatars.mds.yandex.net/i?id=416f71aa75520f929587b79b4333d552c6b62eda-4841475-images-thumbs&n=13",
      name: 'Рождественский сюрпризво фритюре',
      time: '15 минут',
      rate: 4.2,
      price: 120,

      smallImg: 'https://avatars.mds.yandex.net/i?id=39aa110b1a018f6b45381716ab892efffd0ded84-5235444-images-thumbs&n=13' 

    },
    {
      id: '1',
      image: "https://i.imgur.com/QT78Hua.jpg",
      name: 'Рождественский сюрпризво фритюре',
      time: '15 минут',
      rate: 4.2,
      price: 120,

      smallImg: 'https://avatars.mds.yandex.net/i?id=39aa110b1a018f6b45381716ab892efffd0ded84-5235444-images-thumbs&n=13' 

    },
  ]
  return (
    <div className='flex-1'>
      <Pro></Pro>
       <div className="title d-flex-2">
       <h1 className='title-h1'>Салаты</h1>  
        <p className="title-p">Показать все</p>
       </div>
      <div className="d-flex-2">
        {
          data.map((item, idx) => (
            <div className="card-box" key={idx}>
              <div className="img-hover">
                <p>Смотреть рецепт</p>
                <i class="material-icons">arrow_forward</i>
              </div>
              <img className='image' src={item.image} alt="" />
              <h3 className='product-title'>{item.name}</h3>
              <p className='product-price'>{item.price}</p>
              <div className="small-comp ">
                <p className='time'><i className="material-icons">access_time</i>{item.time}</p>
                <div className='ratein '>
                  <i className="material-icons">star</i>
                  <i className="material-icons">star</i>
                  <i className="material-icons">star</i>
                  <i className="material-icons">star</i>
                  <i className="material-icons">star</i>
                  <i className="rate">{item.rate}</i>
                </div>
              </div>
              <div className="card-bottom">
                <img className='small-img' src={item.smallImg} alt="" />
                <img className='small-img' src={item.smallImg} alt="" />
                <img className='small-img' src={item.smallImg} alt="" />
                <button onClick={()=>onAdd(item)} >Купить ингредиенты</button>
              </div>
            </div>
          ))
        }
      </div>

    </div>

  )
}
