import './Header.css'
import './Link'
import Logo from './Frame.png'
// import React from 'react'


import React, { useState, useRef, useEffect } from "react";
import { NavLink } from 'react-router-dom';


export default function Dropdown(props) {
  const container = useRef();
  const { countCartItems } = props
  const [dropdownState, setDropdownState] = useState({ open: false });

  const handleDropdownClick = () =>
    setDropdownState({ open: !dropdownState.open });

  const handleClickOutside = (e) => {
    if (container.current && !container.current.contains(e.target)) {
      setDropdownState({ open: false });
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);





  return (


    <div className='ddd'>
      <div className="top">
        <ul className="left">

          <li>  <i className="material-icons">call</i>7756</li>
          <li>  <i className="material-icons">location_on</i>
            <div className='div'>
              <button type="button" onClick={handleDropdownClick} >Магазины </button>
              {dropdownState.open && (
                <div >
                  <ul className='ul'>
                    <li><a href="https://goo.gl/maps/HmDw8euKnriUtMK57">Магазин 1</a></li>
                    <li><a href="https://goo.gl/maps/BGCUow54kzpr68gD6">Магазин 2</a></li>
                    <li><a href="https://goo.gl/maps/e65N743WJLSwy9hV8">Магазин 3</a></li>
                    <li><a href="https://goo.gl/maps/rYGx8or3SLHi7ixD8">Магазин 4</a></li>

                  </ul>
                </div>
              )}

            </div>
            <i style={{ color: "white" }} className="material-icons">keyboard_arrow_down</i></li>
          <li>  <i className="material-icons">local_shipping</i>Зона доставки</li>
          <li>  <i className="material-icons">access_time</i>17:00 - 17:45<i style={{ color: "white" }} className="material-icons">keyboard_arrow_down</i></li>
        </ul>
        <ul className="right">
          <li>Шеф-лайм</li>
          
          <NavLink className='NavLink' to='/Vakansii'> <li>Вакансии</li></NavLink>
          <a href=""></a>
          <li>Вопрос-ответ</li>
          <li>Партнерам</li>
          <li>Бонусная программа</li>
        </ul>
      </div>
      <div className="img">
        <img src={Logo} alt="logo " />
      </div>
      <div className="bottom">
        <div className="lf">
          <div className="btnn">
            <i className="material-icons">zoom_in</i>

            <p>Каталог</p>
          </div>
          <div style={
            { width: '341px', height: '50px' }} className="input">

            <input style={
              { border: 'none', padding: "0 15px" }
            } type="text" placeholder='Купить молоко' />
            <i className="material-icons">search</i>

          </div>
        </div>
        <ul className="rg">
          <div className="dropdown aks">
            <button><i className="material-icons skitka">discount</i>Скидки<i className="material-icons">keyboard_arrow_down</i></button>

            <ul className='ull spani'>
              <li>Скидки до <span>12%</span></li>
              <li>Скидки до  <span>35%</span></li>
              <li>Скидки до  <span>15%</span></li>
              <li>Скидки до  <span>40%</span></li>
            </ul>
          </div>
          <li className='bbottom'><i className="material-icons">person</i>Кабинет</li>
          <li><i className="material-icons">favorite</i>Избранное</li>
          <li><i className="material-icons">local_grocery_store</i>

            Корзина{" "}
            {countCartItems ? (
              <button className='add'>{countCartItems}</button>
            ) : ('')}
          </li>

        </ul>
      </div>

    </div>



  )
}
