  const onAdd = (product) => {
    const exist = cartItems.find(x => x.id === product.id);
    if(exist){
      setCartItems(cartItems.map(x => x.id ))
    }


  }
  return (
    <>
    <Header/>

    <div className="d-flex">
      <Main ></Main>
      <Bascet cartItems={cartItems}></Bascet>
    </div>

    <Footer/>
    {/* <Main poducts={products}></Main> */}
    </>
  );
}
export default App;
