import React from 'react'

export default function Bascet(props) {

  const { cartItems, onAdd, onRemove } = props;
  const itemPrice = cartItems.reduce((a, c) => a + c.price * c.qty, 0);
  const { countCartItems } = props
  const [people, setPeople] = useState(data)

  return (

    <>
      {people.map((person) => {
        const { id, name, price, } = person;

        return (
          <div className='basket-cont'>
            <div>{cartItems.length === 0 && <div className='bascet'>Cart is Bush</div>}</div>
            {cartItems.map((item) => (

              <div className='d-flex-3 basket-items' key={id} >

                <div className='item-img'></div>
                <div className="butt">
                  <button onClick={() => onAdd(people)} className='add'>+</button>
                  <div>{item.qty}</div>
                  <button onClick={() => onRemove(people)} className='remove'>-</button>

                </div>
                <div className="item-info">
                  <div className='item-name' >{name}</div>
                  <div className="item-price">{qty * price}руб.  </div>
                </div>

                <div>
                  {/* {item.qty} x ${item.price.toFixed(2)} */}
                </div>
              </div>


            ))}

            <div className="total">
              <div className="total-price">
                <h4>Итого,руб:</h4>
                <h3>{itemPrice}</h3>
              </div>

              <a href="#/cart">
                <button onClick={() => alert('Sizdan' + ' ' + itemPrice + 'руб.')}> <i class="material-icons">shopping_cart</i> Подтвердить заказ</button>  {' '}
                {countCartItems ? (
                  <p> {countCartItems}</p>
                ) : (
                  ''
                )}
              </a>{'   '}
            </div>
          </div>
          // </div>
        )


      }
      )
    }
    </>
 
 
    )
  

}
