import './Header.css'
import './Link'
import Logo from './Frame.png'
// import React from 'react'


import React, { useState, useRef, useEffect } from "react";


export default function Dropdown() {
  const container = useRef();
  const [dropdownState, setDropdownState] = useState({ open: false });

  const handleDropdownClick = () =>
    setDropdownState({ open: !dropdownState.open });

  const handleClickOutside = (e) => {
    if (container.current && !container.current.contains(e.target)) {
      setDropdownState({ open: false });
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);





  return (
 

<div className='ddd'>
     <div className="top">
        <ul className="left">
         
          <li>  <i className="material-icons">call</i>7756</li>
          <li>  <i className="material-icons">location_on</i> 
          <div className='div'>
            <button type="button" onClick={handleDropdownClick} >Магазины </button>
            {dropdownState.open  && (
              <div >
                <ul className='ul'>
                  <li>Item 1</li>
                  <li>Item 2</li>
                  <li>Item 3</li>
                  <li>Item 4</li>
                </ul>
              </div>
            )}

          </div>
          <i style={{ color: "white" }} className="material-icons">keyboard_arrow_down</i></li>
          <li>  <i className="material-icons">local_shipping</i>Зона доставки</li>
          <li>  <i className="material-icons">access_time</i>17:00 - 17:45<i style={{ color: "white" }} className="material-icons">keyboard_arrow_down</i></li>
        </ul>
        <ul className="right">
          <li>Шеф-лайм</li>
          <li>Вакансии</li>
          <li>Вопрос-ответ</li>
          <li>Партнерам</li>
          <li>Бонусная программа</li>
        </ul>
      </div>
      <div className="img">
        <img src={Logo} alt="logo " />
      </div>
      <div className="bottom">
        <div className="lf">
          <div className="btnn">
            <i className="material-icons">zoom_in</i>

            <p>Каталог</p>
          </div>
          <div style={
            { width: '341px', height: '50px' }} className="input">

            <input style={
              { border: 'none', padding: "0 15px" }
            } type="text" placeholder='Купить молоко' />
            <i className="material-icons">search</i>

          </div>
        </div>
        <ul className="rg">
          <li><i className="material-icons skitka">discount</i>Магазины<i style={{ background: "none", color: "#FF5C00" }} className="material-icons">keyboard_arrow_down</i></li>
          <li className='bbottom'><i className="material-icons">person</i>Магазины</li>
          <li><i className="material-icons">favorite</i>Магазины</li>
          <li><i className="material-icons">local_grocery_store</i>Магазины</li>

        </ul>
      </div>

</div>



  )
}
